/**
 *  InDesign CS4 LivePreflight
 */

//Target: Application = All Documents
//turn Live Preflight on
app.preflightOptions.preflightOff = false;

//turn Live Preflight off
app.preflightOptions.preflightOff = true;

//Mostfront Document 
//turn Live Preflight on
app.documents[0].preflightOptions.preflightOff = false;

//turn Live Preflight off
app.documents[0].preflightOptions.preflightOff = true;